extension.install:
$forum_db->add_field('groups', 'g_merge_posts', 'bool', false, 0);

if (!isset($forum_config['o_fancy_merge_posts_silent'])) {
	$query = array(
		'INSERT'	=> 'conf_name, conf_value',
		'INTO'		=> 'config',
		'VALUES'	=> '\'o_fancy_merge_posts_silent\', \'1\''
	);
	$forum_db->query_build($query) or error(__FILE__, __LINE__);
}

if (!isset($forum_config['o_fancy_merge_posts_dm_interval'])) {
	$query = array(
		'INSERT'	=> 'conf_name, conf_value',
		'INTO'		=> 'config',
		'VALUES'	=> '\'o_fancy_merge_posts_dm_interval\', \'0\''
	);
	$forum_db->query_build($query) or error(__FILE__, __LINE__);
}

extension.uninstall:
$forum_db->drop_field('groups', 'g_merge_posts');

// Delete extension options from the config
$query = array(
	'DELETE'	=> 'config',
	'WHERE'		=> 'conf_name in (\'o_fancy_merge_posts_silent\', \'o_fancy_merge_posts_dm_interval\')',
);
$forum_db->query_build($query) or error(__FILE__, __LINE__);

hooks.hook:
			if (!isset($fancy_merge_posts)) {
				if (file_exists($ext_info['path'].'/lang/'.$forum_user['language'].'/'.$ext_info['id'].'.php')) {
					require $ext_info['path'].'/lang/'.$forum_user['language'].'/'.$ext_info['id'].'.php';
				} else {
					require $ext_info['path'].'/lang/English/'.$ext_info['id'].'.php';
				}
			}

			$forum_page['group_count'] = 0;

?>
				<div class="content-head">
					<h2 class="hn"><span><?php echo $fancy_merge_posts['Name'] ?></span></h2>
				</div>
				<fieldset class="frm-group group<?php echo ++$forum_page['group_count'] ?>">
					<div class="sf-set set<?php echo ++$forum_page['item_count'] ?>">
						<div class="sf-box checkbox">
							<span class="fld-input"><input type="checkbox" id="fld<?php echo ++$forum_page['fld_count'] ?>" name="form[fancy_merge_posts_silent]" value="1"<?php if ($forum_config['o_fancy_merge_posts_silent'] == '1') echo ' checked="checked"' ?> /></span>
							<label for="fld<?php echo $forum_page['fld_count'] ?>"><?php echo $fancy_merge_posts['Silent mode desc'] ?></label>
						</div>
					</div>
					<div class="sf-set set<?php echo ++$forum_page['item_count'] ?>">
						<div class="sf-box text">
							<label for="fld<?php echo ++$forum_page['fld_count'] ?>"><span><?php echo $fancy_merge_posts['Dont merge timeout'] ?></span><small><?php echo $fancy_merge_posts['Dont merge timeout hint'] ?></small></label><br />
							<span class="fld-input"><input type="text" id="fld<?php echo $forum_page['fld_count'] ?>" name="form[fancy_merge_posts_dm_interval]" size="50" maxlength="128" value="<?php echo forum_htmlencode($forum_config['o_fancy_merge_posts_dm_interval']) ?>" /></span>
						</div>
					</div>
				</fieldset>


<?php

		if ($group['g_id'] != FORUM_GUEST):
			if (!isset($fancy_merge_posts)) {
				if (file_exists($ext_info['path'].'/lang/'.$forum_user['language'].'/'.$ext_info['id'].'.php')) {
					require $ext_info['path'].'/lang/'.$forum_user['language'].'/'.$ext_info['id'].'.php';
				} else {
					require $ext_info['path'].'/lang/English/'.$ext_info['id'].'.php';
				}
			}
?>
			<div class="mf-item">
				<span class="fld-input"><input type="checkbox" id="fld<?php echo ++$forum_page['fld_count'] ?>" name="merge_posts" value="1"<?php if ($group['g_merge_posts'] == '1') echo ' checked="checked"' ?> /></span>
				<label for="fld<?php echo $forum_page['fld_count'] ?>"><?php echo $fancy_merge_posts['Allow dont merge'] ?></label>
			</div>
<?php	endif;

			$query['SELECT'] .= ', p.id AS post_id, p.poster_id, p.message, p.posted, p.edited';
			$query['JOINS'][] = array(
                'LEFT JOIN' => 'posts AS p',
                'ON'        => 't.last_post_id=p.id AND p.poster_id='.$forum_user['id']
            );

			if (!isset($fancy_merge_posts)) {
				if (file_exists($ext_info['path'].'/lang/'.$forum_user['language'].'/'.$ext_info['id'].'.php')) {
					require $ext_info['path'].'/lang/'.$forum_user['language'].'/'.$ext_info['id'].'.php';
				} else {
					require $ext_info['path'].'/lang/English/'.$ext_info['id'].'.php';
				}
			}

			do {
				if ($forum_user['is_guest'] || $cur_posting['poster_id'] == NULL || $cur_posting['message'] == NULL) {
					break;
				}

				if (($forum_page['is_admmod'] || (isset($forum_user['g_merge_posts']) && intval($forum_user['g_merge_posts'], 10) === 1)) && isset($_POST['dont_merge'])) {
					break;
				}

				if (isset($post_info['have_image']) && ($post_info['have_image'])) {
					break;
				}

				if ((utf8_strlen($message) + utf8_strlen($cur_posting['message'])) > FORUM_MAX_POSTSIZE_BYTES) {
					break;
				}

				$fancyDontMergeTimeout = intval($forum_config['o_fancy_merge_posts_dm_interval'], 10);
				if (($fancyDontMergeTimeout > 0) && ($fancyDontMergeTimeout*60) < (time() - $cur_posting['posted'])) {
					break;
				}

				if (intval($forum_config['o_fancy_merge_posts_silent'], 10) === 1) {
					$mrgMsg = $forum_db->escape($cur_posting['message']."\n\n".$message."\n\n");
					$mrgEdited = 'edited='.$now.', ';
				} else {
					$mrgMsg = $forum_db->escape($cur_posting['message']."\n[merge_posts_bbcode]".sprintf($fancy_merge_posts['Added'], format_time(time(), 0, null, null, true))."[/merge_posts_bbcode]\n\n".$message."\n\n");
					$mrgEdited = '';
				}

				$query = array(
					'UPDATE'    => 'posts',
					'SET'        => "message='$mrgMsg', $mrgEdited posted=$now",
					'WHERE'        => 'id='.$cur_posting['post_id']
				);
				$forum_db->query_build($query) or error(__FILE__, __LINE__);

				// Update topic
				$query = array(
					'UPDATE'	=> 'topics',
					'SET'		=> 'last_post='.$now.', last_post_id='.$cur_posting['post_id'].', last_poster=\''.$forum_db->escape($username).'\'',
					'WHERE'		=> 'id='.$post_info['topic_id']
				);
				$forum_db->query_build($query) or error(__FILE__, __LINE__);

				sync_forum($post_info['forum_id']);

				if (!defined('FORUM_SEARCH_IDX_FUNCTIONS_LOADED')) {
					require FORUM_ROOT.'include/search_idx.php';
				}

				update_search_index('edit', $cur_posting['post_id'], $mrgMsg);

				$query = array(
					'UPDATE'    => 'users',
					'SET'        => 'last_post='.$now,
					'WHERE'        => 'id='.$forum_user['id']
				);
				$forum_db->query_build($query) or error(__FILE__, __LINE__);

				if (!$forum_user['is_guest']) {
					$tracked_topics = get_tracked_topics();
					$tracked_topics['topics'][$tid] = time();
					set_tracked_topics($tracked_topics);
				}

                                ($hook = get_hook('fancy_merge_pre_redirect')) ? eval($hook) : null;

				redirect(forum_link($forum_url['post'], $cur_posting['post_id']), $lang_post['Post redirect']);
			} while(0);

if ($tid != 0) {
	if ($tid != 0 && $forum_page['is_admmod'] || (isset($forum_user['g_merge_posts']) && intval($forum_user['g_merge_posts'], 10) === 1)) {
		// Load LANG
		if (!isset($fancy_merge_posts)) {
			if (file_exists($ext_info['path'].'/lang/'.$forum_user['language'].'/'.$ext_info['id'].'.php')) {
				require $ext_info['path'].'/lang/'.$forum_user['language'].'/'.$ext_info['id'].'.php';
			} else {
				require $ext_info['path'].'/lang/English/'.$ext_info['id'].'.php';
			}
		}

		$forum_page['checkboxes']['dont_merge'] = '<div class="mf-item"><span class="fld-input"><input type="checkbox" id="fld'.(++$forum_page['fld_count']).'" name="dont_merge" value="1"'.(isset($_POST['dont_merge']) ? ' checked="checked"' : '').' /></span> <label for="fld'.$forum_page['fld_count'].'">'.$fancy_merge_posts['Dont Merge'].'</label></div>';
	}
}

if (!defined('PUN_EXTENSIONS_USED') && !empty($pun_extensions_used)) {
	define('PUN_EXTENSIONS_USED', 1);
	echo '<p id="extensions-used">Currently used extensions: '.implode(', ', $pun_extensions_used).'. Copyright &copy; 2008 <a href="http://punbb.informer.com/">PunBB</a></p>';
}
