hooks.hook:
if (isset($forum_head['commonjs'])) {
	unset($forum_head['commonjs']);
}

if (file_exists($ext_info['path'].'/c/'.$forum_user['style'].'/style.css')) {
	$forum_head['fancy_jquery_css'] = '<link rel="stylesheet" type="text/css" media="screen" href="'.$ext_info['url'].'/c/'.$forum_user['style'].'/style.css" />';
} else {
	$forum_head['fancy_jquery_css'] = '<link rel="stylesheet" type="text/css" media="screen" href="'.$ext_info['url'].'/c/Oxygen/style.css" />';
}

$forum_head['fancy_jquery_js'] = '<script type="text/javascript" src="'.$ext_info['url'].'/j/lib.min.js"></script>';
